// MODIFIED: Enhanced validation function
function validateOrder(order) {
    if (!order.items || order.items.length === 0) {
        throw new Error("Order must contain at least one item");
    }
    const total = order.items.reduce((sum, item) => sum + item.price * item.quantity, 0);
    if (total > 2000000) { // MODIFIED: limit increased
        throw new Error("Order total exceeds maximum allowed amount");
    }
    // ADDED: New validation
    if (order.customer && !order.emailVerified) {
        throw new Error("Customer email must be verified");
    }
    return true;
}

function formatCurrency(amount, currency) {
    return new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: currency
    }).format(amount);
}
// ADDED: New function
function formatNumber(num, decimals) {
    return num.toFixed(decimals).replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

// Track page views and user interactions
function trackEvent(eventName, properties) {
    if (window.analytics) {
        window.analytics.track(eventName, properties);
    }
}
